
public class CastingExTest 
{

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		//정수
		int n=0;//선언
		//n=0;//초기화
		
		double doubleNum;
		doubleNum=54.321d;
		
		n=(int)doubleNum;
		
		System.out.println(n);
		System.out.println();
		
		doubleNum=(double)n;
		System.out.println(doubleNum);

	}

}
